import{_ as e,o as a,c as n,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 32.1-47.1 Vaccination of children; plan enhancements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-47.1.md","filePath":"vacode/32.1-47.1.md"}'),o={name:"vacode/32.1-47.1.md"},r=t('<h1 id="§-32-1-47-1-vaccination-of-children-plan-enhancements" tabindex="-1">§ 32.1-47.1 Vaccination of children; plan enhancements. <a class="header-anchor" href="#§-32-1-47-1-vaccination-of-children-plan-enhancements" aria-label="Permalink to &quot;§ 32.1-47.1 Vaccination of children; plan enhancements.&quot;">​</a></h1><p>The Department shall include in its vaccination plans procedures to ensure the prompt vaccination of all persons of school age in the Commonwealth, without preference regarding the manner of compliance with the compulsory school attendance law set forth in § <a href="http://law.lis.virginia.gov/vacode/22.1-254/" target="_blank" rel="noreferrer">22.1-254</a>, upon declaration of a public health emergency involving a vaccine-preventable disease and consent of the parent or guardian of the person of school age if such person is a minor or, if the person of school age is not a minor, of the person. Vaccination plans developed pursuant to this section shall be consistent with applicable guidelines developed by the Centers for Disease Control and Prevention, and shall be subject to the same review and update requirements, process, and schedule as the State Emergency Operations Plan developed by the Department of Emergency Management pursuant to § <a href="http://law.lis.virginia.gov/vacode/44-146.18/" target="_blank" rel="noreferrer">44-146.18</a>.</p><p>2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0073" target="_blank" rel="noreferrer">73</a>.</p>',3),i=[r];function c(s,l,h,p,d,f){return a(),n("div",null,i)}const u=e(o,[["render",c]]);export{m as __pageData,u as default};
