import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 32.1-48.015 Authorization to disclose health records.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-48.015.md","filePath":"vacode/32.1-48.015.md"}'),r={name:"vacode/32.1-48.015.md"},i=a('<h1 id="§-32-1-48-015-authorization-to-disclose-health-records" tabindex="-1">§ 32.1-48.015 Authorization to disclose health records. <a class="header-anchor" href="#§-32-1-48-015-authorization-to-disclose-health-records" aria-label="Permalink to &quot;§ 32.1-48.015 Authorization to disclose health records.&quot;">​</a></h1><p>A. The provisions of this article are hereby declared to be necessary to prevent serious harm and serious threats to the health and safety of individuals and the public in Virginia for purposes of authorizing the State Health Commissioner or his designee to examine and review any health records of any person or persons subject to any order of quarantine or order of isolation pursuant to this article and the regulations of the Department of Health and Human Services promulgated in compliance with the Health Insurance Portability and Accountability Act of 1996, as amended. The State Health Commissioner shall authorize any designee in writing to so examine and review any health records of any person or persons subject to any order of quarantine or order of isolation pursuant to this article.</p><p>B. Pursuant to the regulations concerning patient privacy promulgated by the federal Department of Health and Human Services, covered entities may disclose protected health information to the State Health Commissioner or his designee without obtaining consent or authorization for such disclosure from the person who is the subject of the records. Such protected health information shall be used to facilitate the health care of any person or persons who are subject to an order of quarantine or an order of isolation. The State Health Commissioner or his designee shall only redisclose such protected health information in compliance with the aforementioned federal regulations. Further, the protected health information disclosed to the State Health Commissioner or his designee shall be held confidential and shall not be disclosed pursuant to the provisions of subdivision 12 of § <a href="/vacode/2.2-3705.5/">2.2-3705.5</a>.</p><p>C. Pursuant to subsection G of § <a href="/vacode/32.1-116.3/">32.1-116.3</a>, any person requesting or requiring any employee of a public safety agency as defined in subsection M of § <a href="/vacode/32.1-45.2/">32.1-45.2</a> to arrest, transfer, or otherwise exercise custodial supervision over an individual known to the requesting person (i) to be infected with any communicable disease or (ii) to be subject to an order of quarantine or an order of isolation pursuant to Article 3.02 (§ <a href="/vacode/32.1-48.05/">32.1-48.05</a> et seq.) of Chapter 2 shall inform such employee of a public safety agency of the potential risk of exposure to a communicable disease.</p><p>2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0773" target="_blank" rel="noreferrer">773</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP1021" target="_blank" rel="noreferrer">1021</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0699" target="_blank" rel="noreferrer">699</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0783" target="_blank" rel="noreferrer">783</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0778" target="_blank" rel="noreferrer">778</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0502" target="_blank" rel="noreferrer">502</a>.</p>',5),n=[i];function s(h,l,c,d,f,u){return o(),t("div",null,n)}const m=e(r,[["render",s]]);export{g as __pageData,m as default};
