import{_ as e,o as r,c as o,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 32.1-48.017 Use of public or private property or facilities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-48.017.md","filePath":"vacode/32.1-48.017.md"}'),i={name:"vacode/32.1-48.017.md"},a=t('<h1 id="§-32-1-48-017-use-of-public-or-private-property-or-facilities" tabindex="-1">§ 32.1-48.017 Use of public or private property or facilities. <a class="header-anchor" href="#§-32-1-48-017-use-of-public-or-private-property-or-facilities" aria-label="Permalink to &quot;§ 32.1-48.017 Use of public or private property or facilities.&quot;">​</a></h1><p>A. Upon the declaration by the Governor of a state of emergency pursuant to § <a href="http://law.lis.virginia.gov/vacode/44-146.17/" target="_blank" rel="noreferrer">44-146.17</a>, the State Health Commissioner, acting in concert with the Governor, shall be authorized to require the use of any public or private property, building or facility to implement any order of quarantine or order of isolation. The State Health Commissioner and the Governor shall find, together, that the use of the property, building or facility is necessary and appropriate to enforce an order of quarantine or an order of isolation in the least restrictive environment.</p><p>B. If the Commissioner and the Governor elect to use any public or private property, building or facility pursuant to this article and this section, the Commissioner shall make accommodations, in conjunction with the owner or operator of the property, building or facility, for persons who are employed in, using or occupying the property, building or facility and who are not covered by the relevant order of quarantine or order of isolation.</p><p>C. Owners or operators of any property, building or facility so commandeered shall be entitled to compensation.</p><p>2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0773" target="_blank" rel="noreferrer">773</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP1021" target="_blank" rel="noreferrer">1021</a>.</p>',5),n=[a];function p(s,l,c,f,h,d){return r(),o("div",null,n)}const m=e(i,[["render",p]]);export{_ as __pageData,m as default};
