import{_ as e,o,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 32.1-48.03 Petition for hearing; temporary detention.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-48.03.md","filePath":"vacode/32.1-48.03.md"}'),n={name:"vacode/32.1-48.03.md"},i=r('<h1 id="§-32-1-48-03-petition-for-hearing-temporary-detention" tabindex="-1">§ 32.1-48.03 Petition for hearing; temporary detention. <a class="header-anchor" href="#§-32-1-48-03-petition-for-hearing-temporary-detention" aria-label="Permalink to &quot;§ 32.1-48.03 Petition for hearing; temporary detention.&quot;">​</a></h1><p>A. Upon receiving a verified report or upon receiving medical evidence that any person who has been counseled pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-48.02/" target="_blank" rel="noreferrer">32.1-48.02</a> has continued to engage in at-risk behavior, the Commissioner or his designee may petition the general district court of the county or city in which such person resides to order the person to appear before the court to determine whether isolation is necessary to protect the public health from the risk of infection with a communicable disease of public health significance.</p><p>B. If such person cannot be conveniently brought before the court, the court may issue an order of temporary detention. The officer executing the order of temporary detention shall order such person to remain confined in his home or another&#39;s residence or in some convenient and willing institution or other willing place for a period not to exceed 48 hours prior to a hearing. An electronic device may be used to enforce such detention in the person&#39;s home or another&#39;s residence. The institution or other place of temporary detention shall not include a jail or other place of confinement for persons charged with criminal offenses.</p><p>If the specified 48-hour period terminates on a Saturday, Sunday, legal holiday or day on which the court is lawfully closed, such person may be detained until the next day which is not a Saturday, Sunday, legal holiday or day on which the court is lawfully closed.</p><p>C. Any person ordered to appear before the court pursuant to this section shall be informed of his right to be represented by counsel. The court shall provide the person with reasonable opportunity to employ counsel at his own expense, if so requested. If the person is not represented by counsel, the court shall appoint an attorney-at-law to represent him. Counsel so appointed shall be paid a fee of $75 and his necessary expenses.</p><p>1990, c. 958; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0837" target="_blank" rel="noreferrer">837</a>; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0773" target="_blank" rel="noreferrer">773</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP1021" target="_blank" rel="noreferrer">1021</a>.</p>',6),a=[i];function s(h,c,l,p,d,f){return o(),t("div",null,a)}const m=e(n,[["render",s]]);export{g as __pageData,m as default};
