import{_ as e,o as i,c as o,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 32.1-48 Powers of Commissioner in epidemic.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-48.md","filePath":"vacode/32.1-48.md"}'),r={name:"vacode/32.1-48.md"},t=a('<h1 id="§-32-1-48-powers-of-commissioner-in-epidemic" tabindex="-1">§ 32.1-48 Powers of Commissioner in epidemic. <a class="header-anchor" href="#§-32-1-48-powers-of-commissioner-in-epidemic" aria-label="Permalink to &quot;§ 32.1-48 Powers of Commissioner in epidemic.&quot;">​</a></h1><p>A. Nothing in this article shall preclude the Commissioner from requiring immediate immunization of all persons in case of an epidemic of any disease of public health importance for which a vaccine exists other than a person to whose health the administration of a vaccine would be detrimental as certified in writing by a physician licensed to practice medicine in this Commonwealth.</p><p>B. In addition, the State Health Commissioner shall hold the powers conferred pursuant to Article 3.02 (§ <a href="http://law.lis.virginia.gov/vacode/32.1-48.05/" target="_blank" rel="noreferrer">32.1-48.05</a> et seq.) of this chapter to issue orders of quarantine or prepare orders of isolation for a communicable disease of public health threat.</p><p>1979, c. 711; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0773" target="_blank" rel="noreferrer">773</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP1021" target="_blank" rel="noreferrer">1021</a>.</p>',4),n=[t];function s(c,l,h,m,p,d){return i(),o("div",null,n)}const u=e(r,[["render",s]]);export{_ as __pageData,u as default};
