import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 32.1-59 Examination and treatment in certain institutions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-59.md","filePath":"vacode/32.1-59.md"}'),i={name:"vacode/32.1-59.md"},r=n('<h1 id="§-32-1-59-examination-and-treatment-in-certain-institutions" tabindex="-1">§ 32.1-59 Examination and treatment in certain institutions. <a class="header-anchor" href="#§-32-1-59-examination-and-treatment-in-certain-institutions" aria-label="Permalink to &quot;§ 32.1-59 Examination and treatment in certain institutions.&quot;">​</a></h1><p>Every person admitted to any state correctional institution and every person admitted to a state hospital or training center operated by the Department of Behavioral Health and Developmental Services shall be examined and tested for venereal disease. If the person is found to be infected with a venereal disease, the person in charge of such institution or state hospital or training center shall promptly provide treatment and shall report such case as provided in § <a href="http://law.lis.virginia.gov/vacode/32.1-37/" target="_blank" rel="noreferrer">32.1-37</a>.</p><p>Code 1950, § 32-104; 1979, c. 711; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0476" target="_blank" rel="noreferrer">476</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0507" target="_blank" rel="noreferrer">507</a>.</p>',3),o=[r];function s(c,d,l,p,_,h){return t(),a("div",null,o)}const v=e(i,[["render",s]]);export{f as __pageData,v as default};
