import{_ as e,o as i,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 32.1-67.1 Confidentiality of records; prohibition of discrimination.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-67.1.md","filePath":"vacode/32.1-67.1.md"}'),t={name:"vacode/32.1-67.1.md"},o=r('<h1 id="§-32-1-67-1-confidentiality-of-records-prohibition-of-discrimination" tabindex="-1">§ 32.1-67.1 Confidentiality of records; prohibition of discrimination. <a class="header-anchor" href="#§-32-1-67-1-confidentiality-of-records-prohibition-of-discrimination" aria-label="Permalink to &quot;§ 32.1-67.1 Confidentiality of records; prohibition of discrimination.&quot;">​</a></h1><p>The results of the newborn screening services conducted pursuant to this article may be used for research and collective statistical purposes. No publication of research or statistical data shall be made that identifies any infant having a heritable or genetic disorder.</p><p>The Commissioner may authorize linkages between secure electronic data systems maintained by the Department of Health containing newborn screening records and the Virginia Immunization Information System (VIIS) operated pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-46.01/" target="_blank" rel="noreferrer">32.1-46.01</a>. The Commissioner may authorize health care providers authorized to view VIIS to view the newborn screening records of individuals to whom the providers are providing health care services. The records may be made available until the child reaches seven years of age, after which the records shall not be made available through a linkage to VIIS. Such linkages shall be subject to all applicable state and federal privacy laws and regulations.</p><p>1988, c. 97; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0717" target="_blank" rel="noreferrer">717</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0721" target="_blank" rel="noreferrer">721</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0147" target="_blank" rel="noreferrer">147</a>.</p>',4),n=[o];function s(c,l,d,h,f,p){return i(),a("div",null,n)}const g=e(t,[["render",s]]);export{_ as __pageData,g as default};
