import{_ as e,o as i,c as o,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 32.1-69.4 Publication of information regarding cord blood education.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-69.4.md","filePath":"vacode/32.1-69.4.md"}'),l={name:"vacode/32.1-69.4.md"},n=a('<h1 id="§-32-1-69-4-publication-of-information-regarding-cord-blood-education" tabindex="-1">§ 32.1-69.4 Publication of information regarding cord blood education. <a class="header-anchor" href="#§-32-1-69-4-publication-of-information-regarding-cord-blood-education" aria-label="Permalink to &quot;§ 32.1-69.4 Publication of information regarding cord blood education.&quot;">​</a></h1><p>In addition to the requirements of § <a href="http://law.lis.virginia.gov/vacode/32.1-69.3/" target="_blank" rel="noreferrer">32.1-69.3</a>, the Commissioner shall make publicly available, by posting on the public website of the Department of Health, resources relating to umbilical cord blood that have been developed by the Parent&#39;s Guide to Cord Blood Foundation and include the following information:</p><ol><li><p>An explanation of the potential value and uses of umbilical cord blood, including cord blood cells and stem cells, for individuals who are, as well as individuals who are not, biologically related to a mother or her newborn child.</p></li><li><p>An explanation of the differences between using one&#39;s own cord blood cells and using related or unrelated cord blood stem cells in the treatment of disease.</p></li><li><p>An explanation of the differences between public and private umbilical cord blood banking.</p></li><li><p>The options available to a mother relating to stem cells that are contained in the umbilical cord blood after the delivery of her newborn, including (i) donating the stem cells to a public umbilical cord blood bank where facilities are available; (ii) storing the stem cells in a private family umbilical cord blood bank for use by immediate and extended family members; (iii) storing the stem cells for immediate or extended family members through a family or sibling donor banking program that provides free collection, processing, and storage where there is an existing medical need; and (iv) discarding the stem cells.</p></li><li><p>The medical processes involved in the collection of cord blood.</p></li><li><p>Medical or family history criteria that can impact a family&#39;s consideration of umbilical cord blood banking, including the likelihood of using a baby&#39;s cord blood to serve as a match for a family member who has a medical condition.</p></li><li><p>Options for ownership and future use of donated umbilical cord blood.</p></li><li><p>The average cost of public and private umbilical cord blood banking.</p></li><li><p>The availability of public and private cord blood banks to Virginians, including (i) a list of public cord blood banks and the hospitals served by such banks; (ii) a list of private cord blood banks that are available; and (iii) the availability of free family banking and sibling donor programs where there is an existing medical need by a family member.</p></li><li><p>An explanation of which racial and ethnic groups are in particular need of publicly donated cord blood samples based upon medical data developed by the U.S. Health Resources and Services Administration.</p></li></ol><p>2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0069" target="_blank" rel="noreferrer">69</a>.</p>',4),t=[n];function r(d,c,s,b,p,h){return i(),o("div",null,t)}const u=e(l,[["render",r]]);export{f as __pageData,u as default};
