import{_ as e,o as t,c as i,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 32.1-71.02 Notification of cancer patients of statewide cancer registry reporting.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-71.02.md","filePath":"vacode/32.1-71.02.md"}'),a={name:"vacode/32.1-71.02.md"},n=r('<h1 id="§-32-1-71-02-notification-of-cancer-patients-of-statewide-cancer-registry-reporting" tabindex="-1">§ 32.1-71.02 Notification of cancer patients of statewide cancer registry reporting. <a class="header-anchor" href="#§-32-1-71-02-notification-of-cancer-patients-of-statewide-cancer-registry-reporting" aria-label="Permalink to &quot;§ 32.1-71.02 Notification of cancer patients of statewide cancer registry reporting.&quot;">​</a></h1><p>A. Any physician diagnosing a malignant tumor or cancer shall, at such time and in such manner as considered appropriate by such physician, notify each patient whose name and record abstract is required to be reported to the statewide cancer registry pursuant to § <a href="http://law.lis.virginia.gov/vacode/32.1-70/" target="_blank" rel="noreferrer">32.1-70</a> that personal identifying information about him has been included in the registry as required by law. Any physician required to so notify a patient that personal identifying information about him has been included in the cancer registry may, when, in the opinion of the physician, such notice would be injurious to the patient&#39;s health or well-being, provide the required notice to the patient&#39;s authorized representative or next of kin in lieu of notifying the patient.</p><p>B. Upon request to the statewide cancer registry, the patient whose personal identifying information has been submitted to such registry shall have a right to know the identity of the reporter of his information to such registry.</p><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0918" target="_blank" rel="noreferrer">918</a>; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0540" target="_blank" rel="noreferrer">540</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0548" target="_blank" rel="noreferrer">548</a>.</p>',4),o=[n];function s(c,h,p,d,f,l){return t(),i("div",null,o)}const _=e(a,[["render",s]]);export{u as __pageData,_ as default};
