import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 32.1-73.15 Powers and duties of the Council.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-73.15.md","filePath":"vacode/32.1-73.15.md"}'),o={name:"vacode/32.1-73.15.md"},s=i('<h1 id="§-32-1-73-15-powers-and-duties-of-the-council" tabindex="-1">§ 32.1-73.15 Powers and duties of the Council. <a class="header-anchor" href="#§-32-1-73-15-powers-and-duties-of-the-council" aria-label="Permalink to &quot;§ 32.1-73.15 Powers and duties of the Council.&quot;">​</a></h1><p>The Council shall have the power and duty to:</p><ol><li><p>Within the first year, hold public hearings and make inquiries of and solicit comments from the public to assist the Council in understanding the scope of rare diseases in the Commonwealth and the impact of rare diseases on individuals in the Commonwealth.</p></li><li><p>Conduct research and consult with experts to develop policy recommendations related to:</p></li></ol><p>a. Improving access to health care and other services for individuals with rare diseases, including access to health insurance, specialists, health care services, and other necessary services for individuals with rare diseases;</p><p>b. The impact of health insurance coverage, cost sharing, tiers, or other utilization management procedures on access to health care and other necessary services; and</p><p>c. The impact of providing coverage under the state program for medical assistance services for approved health care services and medications for rare diseases.</p><ol start="3"><li><p>Publish a list of existing publicly accessible resources on research, diagnosis, treatment, and education relating to rare diseases on the Council&#39;s webpage.</p></li><li><p>Submit annually by October 1 a report to the Governor and the General Assembly for publication as a report document as provided in the procedures of the Division of Legislative Automated Systems for the processing of legislative documents and reports. The annual report shall (i) describe the activities and recommendations of the Council and (ii) describe the status of funding available to the Council, including information regarding any grants applied for and received by the Council.</p></li><li><p>Apply for, accept, and expend gifts, grants, and donations from public or private sources to enable the Council to better carry out its objectives.</p></li></ol><p>2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0303" target="_blank" rel="noreferrer">303</a>.</p>',8),r=[s];function n(c,l,d,h,p,u){return a(),t("div",null,r)}const v=e(o,[["render",n]]);export{m as __pageData,v as default};
