import{_ as n,o as a,c as s,z as e,a as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 32.1-73.17 Rare Disease Council Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/32.1-73.17.md","filePath":"vacode/32.1-73.17.md"}'),r={name:"vacode/32.1-73.17.md"},o=e("h1",{id:"§-32-1-73-17-rare-disease-council-fund",tabindex:"-1"},[t("§ 32.1-73.17 Rare Disease Council Fund. "),e("a",{class:"header-anchor",href:"#§-32-1-73-17-rare-disease-council-fund","aria-label":'Permalink to "§ 32.1-73.17 Rare Disease Council Fund."'},"​")],-1),i=e("p",null,'There is hereby created in the state treasury a special nonreverting fund to be known as the Rare Disease Council Fund, referred to in this section as "the Fund." The Fund shall be established on the books of the Comptroller. All funds appropriated for such purpose and any gifts, grants, donations, and other funds received on its behalf shall be paid into the state treasury and credited to the Fund. Interest earned on moneys in the Fund shall remain in the Fund and be credited to it. Any moneys remaining in the Fund, including interest thereon, at the end of each fiscal year shall not revert to the general fund but shall remain in the Fund. Moneys in the Fund shall be used solely for the purpose of (i) funding research related to rare diseases and the development of new treatments for rare diseases and supports for persons with rare diseases in the Commonwealth and (ii) supporting the work of the Council. Expenditures and disbursements from the Fund shall be made by the State Treasurer on warrants issued by the Comptroller upon written request signed by the Commissioner of Health.',-1),d=e("p",null,[t("2021, Sp. Sess. I, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0303",target:"_blank",rel:"noreferrer"},"303"),t(".")],-1),l=[o,i,d];function h(u,c,p,f,m,_){return a(),s("div",null,l)}const y=n(r,[["render",h]]);export{g as __pageData,y as default};
