import{_ as e,o as i,c as n,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 33.2-104 English units of measure.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-104.md","filePath":"vacode/33.2-104.md"}'),t={name:"vacode/33.2-104.md"},a=r('<h1 id="§-33-2-104-english-units-of-measure" tabindex="-1">§ 33.2-104 English units of measure. <a class="header-anchor" href="#§-33-2-104-english-units-of-measure" aria-label="Permalink to &quot;§ 33.2-104 English units of measure.&quot;">​</a></h1><p>A. Neither the Commissioner of Highways nor the Department shall expend any funds whatsoever for the purpose of (i) converting the units of measure displayed on any highway sign from English units of measure to metric units of measure, (ii) replacing any highway sign displaying English units of measure with one bearing metric units of measure, or (iii) replacing any highway sign displaying English units of measure with one bearing both English and metric units of measure.</p><p>B. The Board, Commissioner of Highways, and Department shall use English units of measure in the design, advertisement, construction, and preparation of plans and specifications of every highway, bridge, tunnel, or overpass construction or maintenance project. However, nothing in this section shall prevent the Board, Commissioner of Highways, or Department from continuing the use of metric units of measure in the design, advertisement, or construction of any project or the preparation of plans or specifications for a project if, prior to July 1, 1999, metric units of measure were used in the design, advertisement, plans, or specifications for the project.</p><p>1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0052" target="_blank" rel="noreferrer">52</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0086" target="_blank" rel="noreferrer">86</a>, § 33.1-190.2; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0315" target="_blank" rel="noreferrer">315</a>, § 33.1-190.3; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',4),o=[a];function s(h,c,l,f,g,p){return i(),n("div",null,o)}const d=e(t,[["render",s]]);export{m as __pageData,d as default};
