import{_ as t,o as i,c as o,z as e,a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 33.2-105 Evidence as to existence of a public highway.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-105.md","filePath":"vacode/33.2-105.md"}'),s={name:"vacode/33.2-105.md"},n=e("h1",{id:"§-33-2-105-evidence-as-to-existence-of-a-public-highway",tabindex:"-1"},[a("§ 33.2-105 Evidence as to existence of a public highway. "),e("a",{class:"header-anchor",href:"#§-33-2-105-evidence-as-to-existence-of-a-public-highway","aria-label":'Permalink to "§ 33.2-105 Evidence as to existence of a public highway."'},"​")],-1),h=e("p",null,"When a way has been worked by highway officials as a public highway and is used by the public as such, proof of these facts shall be prima facie evidence that the same is a public highway. And when a way has been regularly or periodically worked by highway officials as a public highway and used by the public as such continuously for a period of 20 years, proof of these facts shall be conclusive evidence that the same is a public highway. In all such cases, the center of the general line of passage, conforming to the ancient landmarks where such exist, shall be presumed to be the center of the way and in the absence of proof to the contrary, the width shall be presumed to be 30 feet.",-1),c=e("p",null,"Nothing contained in this section shall be construed to convert into a public highway a way of which the use by the public has been or is permissive and the work thereon by the highway officials has been or is done under permission of the owner of the servient tenement.",-1),r=e("p",null,[a("Code 1950, § 33-98; 1970, c. 322, § 33.1-184; 2014, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805",target:"_blank",rel:"noreferrer"},"805"),a(".")],-1),l=[n,h,c,r];function d(f,p,u,b,y,w){return i(),o("div",null,l)}const m=t(s,[["render",d]]);export{_ as __pageData,m as default};
