import{_ as i,o,c as e,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 33.2-109 Policy of the Commonwealth regarding use of highways by motorcycles; discrimination by political subdivisions prohibited.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-109.md","filePath":"vacode/33.2-109.md"}'),a={name:"vacode/33.2-109.md"},r=t('<h1 id="§-33-2-109-policy-of-the-commonwealth-regarding-use-of-highways-by-motorcycles-discrimination-by-political-subdivisions-prohibited" tabindex="-1">§ 33.2-109 Policy of the Commonwealth regarding use of highways by motorcycles; discrimination by political subdivisions prohibited. <a class="header-anchor" href="#§-33-2-109-policy-of-the-commonwealth-regarding-use-of-highways-by-motorcycles-discrimination-by-political-subdivisions-prohibited" aria-label="Permalink to &quot;§ 33.2-109 Policy of the Commonwealth regarding use of highways by motorcycles; discrimination by political subdivisions prohibited.&quot;">​</a></h1><p>In formulating transportation policy, promulgating regulations, allocating funds, and planning, designing, constructing, equipping, operating, and maintaining transportation facilities, no action of the Board, Commissioner of Highways, or Department shall in any way have the effect of discriminating against motorcycles, motorcycle operators, or motorcycle passengers. No regulation or action of the Board, Commissioner of Highways, or Department shall have the effect of enacting a prohibition or imposing a requirement that applies only to motorcycles or motorcyclists and the principal purpose of which is to restrict or inhibit access of motorcycles and motorcyclists to any highway, bridge, tunnel, or other transportation facility.</p><p>The provisions of this section shall also apply to transportation facilities and projects undertaken or operated by localities and other political subdivisions of the Commonwealth where public funds have been used in whole or in part to plan, design, construct, equip, operate, or maintain the facility or project.</p><p>1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0332" target="_blank" rel="noreferrer">332</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0496" target="_blank" rel="noreferrer">496</a>, § 33.1-13.1; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0068" target="_blank" rel="noreferrer">68</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0083" target="_blank" rel="noreferrer">83</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',4),n=[r];function s(c,l,h,p,g,d){return o(),e("div",null,n)}const y=i(a,[["render",s]]);export{m as __pageData,y as default};
