import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 33.2-1100 Highway construction contracts.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-1100.md","filePath":"vacode/33.2-1100.md"}'),n={name:"vacode/33.2-1100.md"},o=r('<h1 id="§-33-2-1100-highway-construction-contracts" tabindex="-1">§ 33.2-1100 Highway construction contracts. <a class="header-anchor" href="#§-33-2-1100-highway-construction-contracts" aria-label="Permalink to &quot;§ 33.2-1100 Highway construction contracts.&quot;">​</a></h1><p>A. Every contractor whose bid is accepted shall, before commencing work, enter into a contract with the Commissioner of Highways that shall fully set out the time when work shall commence and when the contract shall be completed as well as the time and manner for the payment for the work. Whenever the Commissioner of Highways or his designee publicly opens and announces all bids received for each invitation to bid, it shall be announced at the same time if the lowest read bid exceeds the maximum tolerance of the Department&#39;s estimate for the work represented by that bid.</p><p>B. The contract shall require that the contractor comply with all requirements, conditions, and terms of the contract, including environmental permits that are part of the contract. If the contractor violates a contract provision and the violation results in environmental damage or if the contractor violates environmental laws or environmental permits, the Department may suspend the contractor from future bidding or initiate debarment. In addition, the Department may recover either (i) the loss or damage that the Department suffers as a result of such violation or (ii) any liquidated damages established in such contract plus (iii) reasonable attorney fees and expert witness fees. Any damages and costs collected under this section shall be deposited into the Transportation Trust Fund and used for transportation purposes as determined by the Board.</p><p>Code 1950, § 33-105; 1958, c. 573; 1970, c. 322, § 33.1-191; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0405" target="_blank" rel="noreferrer">405</a>; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0418" target="_blank" rel="noreferrer">418</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0432" target="_blank" rel="noreferrer">432</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0303" target="_blank" rel="noreferrer">303</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',4),i=[o];function s(c,l,h,d,m,p){return t(),a("div",null,i)}const u=e(n,[["render",s]]);export{g as __pageData,u as default};
