import{_ as e,o as i,c as t,O as o}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 33.2-1112 Affidavit to be filed with bid upon work.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-1112.md","filePath":"vacode/33.2-1112.md"}'),a={name:"vacode/33.2-1112.md"},r=o('<h1 id="§-33-2-1112-affidavit-to-be-filed-with-bid-upon-work" tabindex="-1">§ 33.2-1112 Affidavit to be filed with bid upon work. <a class="header-anchor" href="#§-33-2-1112-affidavit-to-be-filed-with-bid-upon-work" aria-label="Permalink to &quot;§ 33.2-1112 Affidavit to be filed with bid upon work.&quot;">​</a></h1><p>Every member of any highway contractors&#39; association who bids upon any work let by the Board or the Commissioner of Highways shall file with his bid an affidavit in substance as follows: that the bidder neither directly nor indirectly has entered into any combination or arrangement with any person, firm, or corporation or entered into any agreement the effect of which is to prevent competition or increase the cost of construction or maintenance of highways or bridges.</p><p>The Board or the Commissioner or Highways shall prescribe the form of this affidavit and no bid shall be accepted unless accompanied by such affidavit.</p><p>Code 1950, § 33-277; 1970, c. 322, § 33.1-343; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0585" target="_blank" rel="noreferrer">585</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0646" target="_blank" rel="noreferrer">646</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',4),n=[r];function s(c,d,f,h,l,p){return i(),t("div",null,n)}const m=e(a,[["render",s]]);export{b as __pageData,m as default};
