import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 33.2-120  Efforts to increase CAFE standards.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-120.md","filePath":"vacode/33.2-120.md"}'),n={name:"vacode/33.2-120.md"},s=r('<h1 id="§-33-2-120-efforts-to-increase-cafe-standards" tabindex="-1">§ 33.2-120 Efforts to increase CAFE standards. <a class="header-anchor" href="#§-33-2-120-efforts-to-increase-cafe-standards" aria-label="Permalink to &quot;§ 33.2-120  Efforts to increase CAFE standards.&quot;">​</a></h1><p>A. As used in this section, unless the context requires a different meaning, &quot;CAFE standards&quot; means the corporate average fuel economy standards for passenger cars and light trucks manufactured for sale in the United States that have been implemented pursuant to the federal Energy Policy and Conservation Act of 1975 (P.L. 94-163), as amended.</p><p>B. It is the policy of the Commonwealth to support federal action that provides for:</p><ol><li><p>An increase in the CAFE standards from the current standard by promoting performance-based tax credits for advanced technology, fuel-efficient vehicles to facilitate the introduction and purchase of such vehicles; and</p></li><li><p>Market incentives and education programs to build demand for high-efficiency, cleaner vehicles, including tax incentives for highly efficient vehicles.</p></li></ol><p>2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0939" target="_blank" rel="noreferrer">939</a>, §§ 67-800, 67-801; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',5),o=[s];function i(c,d,f,l,h,p){return t(),a("div",null,o)}const m=e(n,[["render",i]]);export{u as __pageData,m as default};
