import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 33.2-1227 Violation a nuisance; abatement.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-1227.md","filePath":"vacode/33.2-1227.md"}'),r={name:"vacode/33.2-1227.md"},n=i('<h1 id="§-33-2-1227-violation-a-nuisance-abatement" tabindex="-1">§ 33.2-1227 Violation a nuisance; abatement. <a class="header-anchor" href="#§-33-2-1227-violation-a-nuisance-abatement" aria-label="Permalink to &quot;§ 33.2-1227 Violation a nuisance; abatement.&quot;">​</a></h1><p>Any sign, advertisement, or advertising structure that is erected, used, maintained, operated, posted, or displayed for which no permit has been obtained where such is required, or after revocation or more than 30 days after expiration of a permit, is hereby declared to be a public and private nuisance and may be forthwith removed, obliterated, or abated by the Commissioner of Highways. The Commissioner of Highways may collect the cost of such removal, obliteration, or abatement from the person erecting, using, maintaining, operating, posting, or displaying such sign, advertisement, or advertising structure.</p><p>Code 1950, § 33-321; 1954, c. 588; 1960, c. 406; 1970, c. 322, § 33.1-375; 1976, c. 14; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0760" target="_blank" rel="noreferrer">760</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0818" target="_blank" rel="noreferrer">818</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',3),o=[n];function s(c,d,l,h,p,m){return a(),t("div",null,o)}const f=e(r,[["render",s]]);export{g as __pageData,f as default};
