import{_ as e,o,c as t,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 33.2-1513 Use of proceeds of Notes.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-1513.md","filePath":"vacode/33.2-1513.md"}'),a={name:"vacode/33.2-1513.md"},s=r('<h1 id="§-33-2-1513-use-of-proceeds-of-notes" tabindex="-1">§ 33.2-1513 Use of proceeds of Notes. <a class="header-anchor" href="#§-33-2-1513-use-of-proceeds-of-notes" aria-label="Permalink to &quot;§ 33.2-1513 Use of proceeds of Notes.&quot;">​</a></h1><p>A. The net proceeds of the Notes shall be used exclusively for the purpose of providing funds, together with any other available funds, for paying the costs incurred or to be incurred for construction or funding of such projects to be designated by the Board.</p><p>B. The proceeds of Notes, including any premium received on the sale thereof, shall be made available by the Board to pay costs of the projects and, where appropriate, may be paid to any authority, locality, commission, or other entity for the purposes of paying for costs of the projects. The proceeds of Notes may be used together with any federal, local, or private funds that may be made available for such purpose. The proceeds of Notes, together with any investment earnings thereon, may at the discretion of the Board secure the payment of principal or purchase price of and redemption premium, if any, and interest on Notes.</p><p>2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0830" target="_blank" rel="noreferrer">830</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0868" target="_blank" rel="noreferrer">868</a>, § 33.1-23.16; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',4),i=[s];function n(c,p,d,f,h,l){return o(),t("div",null,i)}const g=e(a,[["render",n]]);export{_ as __pageData,g as default};
