import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 33.2-1515 Form and manner of execution; signature of person ceasing to be officer.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-1515.md","filePath":"vacode/33.2-1515.md"}'),o={name:"vacode/33.2-1515.md"},i=t('<h1 id="§-33-2-1515-form-and-manner-of-execution-signature-of-person-ceasing-to-be-officer" tabindex="-1">§ 33.2-1515 Form and manner of execution; signature of person ceasing to be officer. <a class="header-anchor" href="#§-33-2-1515-form-and-manner-of-execution-signature-of-person-ceasing-to-be-officer" aria-label="Permalink to &quot;§ 33.2-1515 Form and manner of execution; signature of person ceasing to be officer.&quot;">​</a></h1><p>The Notes shall be signed on behalf of the Board by the chairman or vice-chairman of the Board, or shall bear the facsimile signature of such officer, and shall bear the official seal of the Board, which shall be attested by the manual or facsimile signature of the secretary or assistant secretary of the Board. In the event that Notes shall bear the facsimile signature of the chairman or vice-chairman of the Board, such Notes shall be signed by such administrative assistant as the chairman of the Board shall determine or by any registrar/paying agent that may be designated by the Board. In case any officer whose signature or a facsimile of whose signature appears on any Notes shall cease to be such officer before the delivery of such Notes, such signature or facsimile signature nevertheless shall be valid and sufficient for all purposes as if such officer had remained in office until such delivery.</p><p>2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0830" target="_blank" rel="noreferrer">830</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0868" target="_blank" rel="noreferrer">868</a>, § 33.1-23.18; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',3),n=[i];function s(c,f,h,l,d,g){return a(),r("div",null,n)}const _=e(o,[["render",s]]);export{m as __pageData,_ as default};
