import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 33.2-1524.1 Transportation Trust Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-1524.1.md","filePath":"vacode/33.2-1524.1.md"}'),r={name:"vacode/33.2-1524.1.md"},n=o('<h1 id="§-33-2-1524-1-transportation-trust-fund" tabindex="-1">§ 33.2-1524.1 Transportation Trust Fund. <a class="header-anchor" href="#§-33-2-1524-1-transportation-trust-fund" aria-label="Permalink to &quot;§ 33.2-1524.1 Transportation Trust Fund.&quot;">​</a></h1><p>There is hereby created in the Department of Treasury a special nonreverting fund to be known as the Transportation Trust Fund, consisting of funds distributed from the Commonwealth Transportation Fund pursuant to § <a href="/vacode/33.2-1524/">33.2-1524</a>. The revenues deposited pursuant to subdivision B 1 of § <a href="/vacode/33.2-1524/">33.2-1524</a> shall be distributed during the year to result in the following:</p><ol><li><p>For construction programs pursuant to § <a href="/vacode/33.2-358/">33.2-358</a>, 53 percent;</p></li><li><p>To the Commonwealth Mass Transit Fund established pursuant to § <a href="/vacode/33.2-1526/">33.2-1526</a>, 23 percent;</p></li><li><p>To the Commonwealth Rail Fund established pursuant to § <a href="/vacode/33.2-1526.4/">33.2-1526.4</a>, 7.5 percent;</p></li><li><p>To the Commonwealth Port Fund established pursuant to § <a href="/vacode/33.2-1526.5/">33.2-1526.5</a>, 2.5 percent;</p></li><li><p>To the Commonwealth Aviation Fund established pursuant to § <a href="/vacode/33.2-1526.6/">33.2-1526.6</a>, 1.5 percent;</p></li><li><p>To the Commonwealth Space Flight Fund established pursuant to § <a href="/vacode/33.2-1526.7/">33.2-1526.7</a>, one percent;</p></li><li><p>To the Priority Transportation Fund established pursuant to § <a href="/vacode/33.2-1527/">33.2-1527</a>, 10.5 percent; and</p></li><li><p>To a special fund within the Commonwealth Transportation Fund in the state treasury, one percent to be used to meet the necessary expenses of the Department of Motor Vehicles.</p></li></ol><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1230" target="_blank" rel="noreferrer">1230</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1275" target="_blank" rel="noreferrer">1275</a>.</p>',4),i=[n];function s(p,l,d,u,h,c){return t(),a("div",null,i)}const m=e(r,[["render",s]]);export{_ as __pageData,m as default};
