import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 33.2-1526.2 Commonwealth Transit Capital Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-1526.2.md","filePath":"vacode/33.2-1526.2.md"}'),o={name:"vacode/33.2-1526.2.md"},i=n('<h1 id="§-33-2-1526-2-commonwealth-transit-capital-fund" tabindex="-1">§ 33.2-1526.2 Commonwealth Transit Capital Fund. <a class="header-anchor" href="#§-33-2-1526-2-commonwealth-transit-capital-fund" aria-label="Permalink to &quot;§ 33.2-1526.2 Commonwealth Transit Capital Fund.&quot;">​</a></h1><p>A. There is hereby created in the Department of the Treasury a special nonreverting fund known as the Commonwealth Transit Capital Fund. The Commonwealth Transit Capital Fund shall be a subaccount of the Commonwealth Mass Transit Fund.</p><p>B. The Commonwealth Transit Capital Fund subaccount shall be established on the books of the Comptroller and consist of such moneys as are appropriated to it by the General Assembly and of all donations, gifts, bequests, grants, endowments, and other moneys given, bequeathed, granted, or otherwise made available to the Commonwealth Transit Capital Fund. Any funds remaining in the Commonwealth Transit Capital Fund at the end of the biennium shall not revert to the general fund, but shall remain in the Commonwealth Transit Capital Fund. Interest earned on funds within the Commonwealth Transit Capital Fund shall remain in and be credited to the Commonwealth Transit Capital Fund.</p><p>C. Proceeds of the Commonwealth Transit Capital Fund may be paid to any political subdivision, another public entity created by an act of the General Assembly, or a private entity as defined in § <a href="/vacode/33.2-1800/">33.2-1800</a> and for purposes as enumerated in subdivision 7 of § <a href="/vacode/33.2-1701/">33.2-1701</a> or expended by the Department of Rail and Public Transportation for the purposes specified in this subsection. Revenues of the Commonwealth Transit Capital Fund shall be used to support capital expenditures involving the establishment, improvement, or expansion of public transportation services through specific projects approved by the Commonwealth Transportation Board.</p><p>D. The Commonwealth Transit Capital Fund shall not be allocated without requiring a local match from the recipient.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1230" target="_blank" rel="noreferrer">1230</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1275" target="_blank" rel="noreferrer">1275</a>.</p>',6),r=[i];function s(l,h,d,p,m,c){return a(),t("div",null,r)}const C=e(o,[["render",s]]);export{f as __pageData,C as default};
