import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 33.2-1526.7 Commonwealth Space Flight Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-1526.7.md","filePath":"vacode/33.2-1526.7.md"}'),o={name:"vacode/33.2-1526.7.md"},r=n('<h1 id="§-33-2-1526-7-commonwealth-space-flight-fund" tabindex="-1">§ 33.2-1526.7 Commonwealth Space Flight Fund. <a class="header-anchor" href="#§-33-2-1526-7-commonwealth-space-flight-fund" aria-label="Permalink to &quot;§ 33.2-1526.7 Commonwealth Space Flight Fund.&quot;">​</a></h1><p>A. There is hereby created in the Department of the Treasury a special nonreverting fund that shall be a part of the Commonwealth Transportation Fund and shall be known as the Commonwealth Space Flight Fund (the Fund). The Fund shall be established on the books of the Comptroller and the funds remaining in such Fund at the end of a biennium shall not revert to the general fund but shall remain in the Fund. Interest earned on such funds shall remain in the Fund and be credited to it.</p><p>B. The amounts allocated to the Commonwealth Space Flight Fund pursuant to § <a href="/vacode/33.2-1524.1/">33.2-1524.1</a> shall be allocated by the Board to the Board of Directors of the Virginia Commercial Space Flight Authority to be used to support the capital needs, maintenance, and operating costs of any and all facilities owned and operated by the Virginia Commercial Space Flight Authority.</p><p>C. Commonwealth Space Flight Fund revenue shall be allocated by the Board of Directors to the Virginia Commercial Space Flight Authority in order to foster and stimulate the growth of the commercial space flight industry in Virginia.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1230" target="_blank" rel="noreferrer">1230</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1275" target="_blank" rel="noreferrer">1275</a>.</p>',5),i=[r];function l(h,d,c,s,p,m){return t(),a("div",null,i)}const g=e(o,[["render",l]]);export{f as __pageData,g as default};
