import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 33.2-1526 Commonwealth Mass Transit Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-1526.md","filePath":"vacode/33.2-1526.md"}'),n={name:"vacode/33.2-1526.md"},i=r('<h1 id="§-33-2-1526-commonwealth-mass-transit-fund" tabindex="-1">§ 33.2-1526 Commonwealth Mass Transit Fund. <a class="header-anchor" href="#§-33-2-1526-commonwealth-mass-transit-fund" aria-label="Permalink to &quot;§ 33.2-1526 Commonwealth Mass Transit Fund.&quot;">​</a></h1><p>A. There is hereby created in the State Treasury a special nonreverting fund that shall be a part of the Transportation Trust Fund and shall be known as the Commonwealth Mass Transit Fund (the Fund). The Fund shall be established on the books of the Comptroller and any funds remaining in the Fund at the end of the biennium shall not revert to the general fund but shall remain in the Fund. Interest earned on such funds shall be credited to the Fund.</p><p>B. The amounts allocated to the Fund pursuant to § <a href="/vacode/33.2-1526.1/">33.2-1526.1</a> shall be used to support the operating, capital, and administrative costs of public transportation at a state share determined by the Board, and such amounts may be used to support the capital project costs of public transportation and ridesharing equipment, facilities, and associated costs at a state share determined by the Board. Capital costs may include debt service payments on local or agency transit bonds.</p><p>1986, Sp. Sess., c. 13, § 33.1-23.03:2; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0905" target="_blank" rel="noreferrer">905</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0907" target="_blank" rel="noreferrer">907</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0779" target="_blank" rel="noreferrer">779</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0817" target="_blank" rel="noreferrer">817</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0684" target="_blank" rel="noreferrer">684</a>; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0299" target="_blank" rel="noreferrer">299</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0345" target="_blank" rel="noreferrer">345</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1230" target="_blank" rel="noreferrer">1230</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1275" target="_blank" rel="noreferrer">1275</a>.</p>',4),o=[i];function s(l,h,c,d,p,g){return a(),t("div",null,o)}const b=e(n,[["render",s]]);export{u as __pageData,b as default};
