import{_ as e,o as a,c as n,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 33.2-1530 Highway Maintenance and Operating Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-1530.md","filePath":"vacode/33.2-1530.md"}'),r={name:"vacode/33.2-1530.md"},i=t('<h1 id="§-33-2-1530-highway-maintenance-and-operating-fund" tabindex="-1">§ 33.2-1530 Highway Maintenance and Operating Fund. <a class="header-anchor" href="#§-33-2-1530-highway-maintenance-and-operating-fund" aria-label="Permalink to &quot;§ 33.2-1530 Highway Maintenance and Operating Fund.&quot;">​</a></h1><p>There is hereby created in the state treasury a special nonreverting fund to be known as the Highway Maintenance and Operating Fund ( the Fund ). The Fund shall be established on the books of the Comptroller. Any moneys remaining in the Fund at the end of each fiscal year shall not revert to the general fund but shall remain in the Fund.</p><p>The Fund shall consist of the following:</p><ol><li><p>Revenues allocated pursuant to subdivision B 1 of § <a href="/vacode/33.2-1524/">33.2-1524</a>;</p></li><li><p>Revenues generated pursuant to § <a href="/vacode/33.2-213/">33.2-213</a>;</p></li><li><p>Right-of-way use fees pursuant to § <a href="/vacode/56-484.32/">56-484.32</a>;</p></li><li><p>Civil penalties pursuant to §§ <a href="/vacode/33.2-216/">33.2-216</a>, <a href="/vacode/33.2-1224/">33.2-1224</a>, <a href="/vacode/33.2-1229/">33.2-1229</a>, <a href="/vacode/46.2-341.20:2/">46.2-341.20:2</a>, <a href="/vacode/46.2-1573/">46.2-1573</a>, <a href="/vacode/46.2-1573.11/">46.2-1573.11</a>, <a href="/vacode/46.2-1573.23/">46.2-1573.23</a>, and <a href="/vacode/46.2-1573.36/">46.2-1573.36</a>;</p></li><li><p>Permit fees as outlined in § <a href="/vacode/46.2-652.1/">46.2-652.1</a>;</p></li><li><p>Permit fees pursuant to §§ <a href="/vacode/46.2-1128/">46.2-1128</a>, <a href="/vacode/46.2-1140.1/">46.2-1140.1</a>, <a href="/vacode/46.2-1142.1/">46.2-1142.1</a>, <a href="/vacode/46.2-1143/">46.2-1143</a>, <a href="/vacode/46.2-1148/">46.2-1148</a>, and <a href="/vacode/46.2-1149.1/">46.2-1149.1</a>;</p></li><li><p>Applicable portions of emissions inspection fees from on-road emissions inspectors as designated in § <a href="/vacode/46.2-1182/">46.2-1182</a>; and</p></li><li><p>Any other funds appropriated by the General Assembly.</p></li></ol><p>In any year in which the Board determines funding in excess of the amount provided pursuant to § <a href="/vacode/33.2-1524/">33.2-1524</a> is necessary for the Special Structure Program pursuant to § <a href="/vacode/33.2-374/">33.2-374</a>, the Board shall allocate moneys from the Fund to the Special Structure Fund established pursuant to § <a href="/vacode/33.2-1532/">33.2-1532</a>.</p><p>2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0684" target="_blank" rel="noreferrer">684</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1230" target="_blank" rel="noreferrer">1230</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1275" target="_blank" rel="noreferrer">1275</a>.</p>',6),o=[i];function s(d,l,h,c,p,f){return a(),n("div",null,o)}const v=e(r,[["render",s]]);export{g as __pageData,v as default};
