import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 33.2-1532 Special Structure Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-1532.md","filePath":"vacode/33.2-1532.md"}'),n={name:"vacode/33.2-1532.md"},i=r('<h1 id="§-33-2-1532-special-structure-fund" tabindex="-1">§ 33.2-1532 Special Structure Fund. <a class="header-anchor" href="#§-33-2-1532-special-structure-fund" aria-label="Permalink to &quot;§ 33.2-1532 Special Structure Fund.&quot;">​</a></h1><p>A. There is hereby created in the state treasury a special nonreverting fund to be known as the Special Structure Fund, referred to in this section as &quot;the Fund.&quot; The Fund shall be established on the books of the Comptroller.</p><p>B. The amount allocated to the Fund pursuant to §§ <a href="/vacode/33.2-1524/">33.2-1524</a> and <a href="/vacode/33.2-1530/">33.2-1530</a> and any funds as may be appropriated by the General Assembly shall be paid into the state treasury and credited to the Fund. Interest earned on moneys in the Fund shall remain in the Fund and be credited to it. Any moneys remaining in the Fund, including interest thereon, at the end of each fiscal year shall not revert to the general fund but shall remain in the Fund.</p><p>C. Moneys in the Fund shall be allocated by the Board and used solely for the purposes of funding maintenance, rehabilitation, and replacement of special structures, as defined in § <a href="/vacode/33.2-374/">33.2-374</a>.</p><p>2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0083" target="_blank" rel="noreferrer">83</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0349" target="_blank" rel="noreferrer">349</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1230" target="_blank" rel="noreferrer">1230</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1275" target="_blank" rel="noreferrer">1275</a>.</p>',5),o=[i];function l(s,d,c,h,u,p){return t(),a("div",null,o)}const g=e(n,[["render",l]]);export{_ as __pageData,g as default};
