import{_ as e,o,c as r,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 33.2-1708 Revenue bonds.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-1708.md","filePath":"vacode/33.2-1708.md"}'),n={name:"vacode/33.2-1708.md"},i=a('<h1 id="§-33-2-1708-revenue-bonds" tabindex="-1">§ 33.2-1708 Revenue bonds. <a class="header-anchor" href="#§-33-2-1708-revenue-bonds" aria-label="Permalink to &quot;§ 33.2-1708 Revenue bonds.&quot;">​</a></h1><p>The Board may provide by resolution, at one time or from time to time, for the issuance of revenue bonds, notes, or other revenue obligations of the Commonwealth for the purpose of paying all or any part of the cost, as defined in § <a href="/vacode/33.2-1700/">33.2-1700</a>, of any one or more projects, as defined in § <a href="/vacode/33.2-1700/">33.2-1700</a>. The principal or purchase price of, and redemption premium, if any, and interest on such obligations shall be payable solely from the special funds herein provided for such payment. For the purposes of this section, &quot;special funds&quot; includes any funds established for Commonwealth of Virginia Toll Revenue Bonds, Commonwealth of Virginia Transportation Contract Revenue Bonds, Commonwealth of Virginia Transportation Revenue Bonds, Commonwealth of Virginia Interstate 81 Program Revenue Bonds, Commonwealth of Virginia Federal Highway Reimbursement Anticipation Notes, or Commonwealth of Virginia Federal Transportation Grant Anticipation Revenue Notes, or Commonwealth of Virginia Passenger Rail Facilities Bonds.</p><p>Code 1950, § 33-236; 1970, c. 322, § 33.1-276; 1988, cc. 844, 903; 1989, Sp. Sess., cc. 9, 11; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1019" target="_blank" rel="noreferrer">1019</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1044" target="_blank" rel="noreferrer">1044</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0830" target="_blank" rel="noreferrer">830</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0868" target="_blank" rel="noreferrer">868</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1230" target="_blank" rel="noreferrer">1230</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1275" target="_blank" rel="noreferrer">1275</a>.</p>',3),t=[i];function s(l,c,f,p,h,d){return o(),r("div",null,t)}const m=e(n,[["render",s]]);export{u as __pageData,m as default};
