import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 33.2-1713 Sale of bonds; bonds as legal investments.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-1713.md","filePath":"vacode/33.2-1713.md"}'),r={name:"vacode/33.2-1713.md"},o=n('<h1 id="§-33-2-1713-sale-of-bonds-bonds-as-legal-investments" tabindex="-1">§ 33.2-1713 Sale of bonds; bonds as legal investments. <a class="header-anchor" href="#§-33-2-1713-sale-of-bonds-bonds-as-legal-investments" aria-label="Permalink to &quot;§ 33.2-1713 Sale of bonds; bonds as legal investments.&quot;">​</a></h1><p>The Board may sell such bonds in such manner and for such price as it may determine to be for the best interests of the Commonwealth, but no such sale shall be made at a price so low as to require the payment of interest on the money received therefor at more than the maximum annual percentage rate approved by the Commonwealth Treasury Board with respect to such obligations in accordance with § <a href="http://law.lis.virginia.gov/vacode/2.2-2416/" target="_blank" rel="noreferrer">2.2-2416</a>.</p><p>All bonds issued pursuant to the authority of this chapter are hereby made securities in which all public officers and bodies of the Commonwealth and all political subdivisions thereof; all insurance companies and associations, all national banks and trust companies, and all savings institutions, including savings and loan associations, in the Commonwealth; and all executors, administrators, trustees, and other fiduciaries, both individual or corporate, may properly and legally invest funds within their control.</p><p>Code 1950, § 33-240; 1952, c. 485; 1958, c. 485; 1970, c. 322, § 33.1-280; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1019" target="_blank" rel="noreferrer">1019</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1044" target="_blank" rel="noreferrer">1044</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0830" target="_blank" rel="noreferrer">830</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0868" target="_blank" rel="noreferrer">868</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',4),i=[o];function s(l,c,d,h,p,f){return a(),t("div",null,i)}const u=e(r,[["render",s]]);export{m as __pageData,u as default};
