import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 33.2-1818 Sovereign immunity.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-1818.md","filePath":"vacode/33.2-1818.md"}'),o={name:"vacode/33.2-1818.md"},r=a('<h1 id="§-33-2-1818-sovereign-immunity" tabindex="-1">§ 33.2-1818 Sovereign immunity. <a class="header-anchor" href="#§-33-2-1818-sovereign-immunity" aria-label="Permalink to &quot;§ 33.2-1818 Sovereign immunity.&quot;">​</a></h1><p>Nothing in this chapter shall be construed as or deemed a waiver of the sovereign immunity of the Commonwealth, any responsible public entity, or any affected locality or public entity or any officer or employee thereof with respect to the participation in or approval of all or any part of the qualifying transportation facility or its operation, including interconnection of the qualifying transportation facility with any other transportation facility. Localities in which a qualifying transportation facility is located shall possess sovereign immunity with respect to its construction and operation.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0855" target="_blank" rel="noreferrer">855</a>, § 56-573; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0647" target="_blank" rel="noreferrer">647</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',3),n=[r];function c(l,s,p,f,h,_){return t(),i("div",null,n)}const d=e(o,[["render",c]]);export{m as __pageData,d as default};
