import{_ as o,o as i,c as n,z as e,a as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 33.2-1917 Protection of employees of public transportation systems.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-1917.md","filePath":"vacode/33.2-1917.md"}'),a={name:"vacode/33.2-1917.md"},r=e("h1",{id:"§-33-2-1917-protection-of-employees-of-public-transportation-systems",tabindex:"-1"},[t("§ 33.2-1917 Protection of employees of public transportation systems. "),e("a",{class:"header-anchor",href:"#§-33-2-1917-protection-of-employees-of-public-transportation-systems","aria-label":'Permalink to "§ 33.2-1917 Protection of employees of public transportation systems."'},"​")],-1),s=e("p",null,[t("In any county or city, the commission referred to in § "),e("a",{href:"http://law.lis.virginia.gov/vacode/33.2-1915/",target:"_blank",rel:"noreferrer"},"33.2-1915"),t(", in addition to other prohibitions, shall not operate any such transit facility, or otherwise provide or cause to be provided any transportation services, unless fair and equitable arrangements have been made for the protection of employees of existing public transportation systems in the transportation district or in the metropolitan area in which the transportation district is located. Such protections shall include (i) assurances of employment to employees of such transportation systems to the fullest extent possible consistent with sound management, and priority of employment or, if terminated or laid off, reemployment; (ii) preservation of rights, privileges, and benefits, including continuation of pension rights and benefits, under existing collective bargaining agreements or otherwise; (iii) continuation of collective bargaining rights; (iv) protection of individual employees against a worsening of their positions with respect to their employment, to the extent provided by 49 U.S.C. § 5333 (b), also known as § 13(c) of the Federal Transit Act; and (v) paid training and retraining programs. Such protections shall be specified by the commission in any contract or lease for the acquisition or operation of any such transit facilities or services. The employees of any transit facility operated by the commission shall have the right, in the case of any labor dispute relating to the terms and conditions of their employment for the purpose of resolving such dispute, to submit the dispute to final and binding arbitration by an impartial umpire or board of arbitration acceptable to the parties.")],-1),c=e("p",null,[t("1974, c. 53, § 15.1-1357.2; 1997, c. 587, § 15.1-4517; 2014, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805",target:"_blank",rel:"noreferrer"},"805"),t(".")],-1),l=[r,s,c];function p(d,h,f,m,u,y){return i(),n("div",null,l)}const v=o(a,[["render",p]]);export{g as __pageData,v as default};
