import{_ as e,o,c as t,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 33.2-206 How testimony of members of Commonwealth Transportation Board and Commissioner of Highways taken in civil proceedings.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-206.md","filePath":"vacode/33.2-206.md"}'),a={name:"vacode/33.2-206.md"},n=i('<h1 id="§-33-2-206-how-testimony-of-members-of-commonwealth-transportation-board-and-commissioner-of-highways-taken-in-civil-proceedings" tabindex="-1">§ 33.2-206 How testimony of members of Commonwealth Transportation Board and Commissioner of Highways taken in civil proceedings. <a class="header-anchor" href="#§-33-2-206-how-testimony-of-members-of-commonwealth-transportation-board-and-commissioner-of-highways-taken-in-civil-proceedings" aria-label="Permalink to &quot;§ 33.2-206 How testimony of members of Commonwealth Transportation Board and Commissioner of Highways taken in civil proceedings.&quot;">​</a></h1><p>No member of the Board or the Commissioner of Highways shall be required to leave his office for the purpose of testifying in any suit, action, or other civil proceeding involving any of his official duties, but the deposition of any member of the Board or the Commissioner of Highways may be taken at the main office of the Board in Richmond, after reasonable notice in writing has been given to the adverse party.</p><p>Any deposition taken pursuant to this section may be read in the pending suit, action, or other civil proceeding. However, on motion to the court, filed at least 10 days before the commencement of the trial, the judge may, for good cause shown, require any member of the Board or the Commissioner of Highways to attend and testify ore tenus.</p><p>Code 1950, § 33-3.1; 1966, c. 369; 1970, c. 322, § 33.1-4; 1974, c. 462; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0195" target="_blank" rel="noreferrer">195</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0223" target="_blank" rel="noreferrer">223</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',4),r=[n];function s(m,c,h,f,d,l){return o(),t("div",null,r)}const _=e(a,[["render",s]]);export{g as __pageData,_ as default};
