import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 33.2-216 Roadside memorials; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-216.md","filePath":"vacode/33.2-216.md"}'),r={name:"vacode/33.2-216.md"},o=i('<h1 id="§-33-2-216-roadside-memorials-penalty" tabindex="-1">§ 33.2-216 Roadside memorials; penalty. <a class="header-anchor" href="#§-33-2-216-roadside-memorials-penalty" aria-label="Permalink to &quot;§ 33.2-216 Roadside memorials; penalty.&quot;">​</a></h1><p>A. The Board shall establish regulations regarding size, distance from the roadway, and other safety concerns to govern the installation, maintenance, and removal of roadside memorials, plaques, and other devices placed within the right-of-way that commemorate the memory of persons killed in vehicle crashes within the right-of-way of any state highway.</p><p>B. Any person who installs any plaque, device, sign, object, material, or other memorial within the right-of-way of any highway controlled by the Department except in accordance with criteria established as provided in this section may be assessed a civil penalty of no more than $100. Each occurrence shall be subject to a separate penalty. All civil penalties collected under this section shall be paid into the Highway Maintenance and Operating Fund established pursuant to § <a href="http://law.lis.virginia.gov/vacode/33.2-1530/" target="_blank" rel="noreferrer">33.2-1530</a>.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0096" target="_blank" rel="noreferrer">96</a>, § 33.1-206.1; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',4),n=[o];function s(l,c,d,h,p,m){return a(),t("div",null,n)}const g=e(r,[["render",s]]);export{f as __pageData,g as default};
