import{_ as t,o as a,c as e,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 33.2-219 Statements to be filed with Commonwealth Transportation Board by transit systems.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-219.md","filePath":"vacode/33.2-219.md"}'),i={name:"vacode/33.2-219.md"},r=o('<h1 id="§-33-2-219-statements-to-be-filed-with-commonwealth-transportation-board-by-transit-systems" tabindex="-1">§ 33.2-219 Statements to be filed with Commonwealth Transportation Board by transit systems. <a class="header-anchor" href="#§-33-2-219-statements-to-be-filed-with-commonwealth-transportation-board-by-transit-systems" aria-label="Permalink to &quot;§ 33.2-219 Statements to be filed with Commonwealth Transportation Board by transit systems.&quot;">​</a></h1><p>Any transit system that conducts its operations within the exclusive jurisdiction of any locality or within the boundaries of any district as defined in § <a href="http://law.lis.virginia.gov/vacode/33.2-1901/" target="_blank" rel="noreferrer">33.2-1901</a>, and any adjoining locality, shall file annually with the Board such financial and other statistical data as the Board shall require in order to effectively administer the provisions of § <a href="http://law.lis.virginia.gov/vacode/46.2-206/" target="_blank" rel="noreferrer">46.2-206</a> and shall file with the Department of Rail and Public Transportation, at such times as the Department of Rail and Public Transportation shall require, such information as the Department of Rail and Public Transportation shall require to carry out its duties under subdivision 4 of § <a href="http://law.lis.virginia.gov/vacode/33.2-285/" target="_blank" rel="noreferrer">33.2-285</a>.</p><p>The provisions of this section shall not be construed so as to exempt any such transit system from any provision of law or regulation made pursuant to law that requires the filing of data with any other agency of the Commonwealth.</p><p>1972, c. 290, § 33.1-223.1; 1984, c. 760; 1992, c. 167; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',4),n=[r];function s(l,h,d,c,f,m){return a(),e("div",null,n)}const u=t(i,[["render",s]]);export{_ as __pageData,u as default};
