import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 33.2-225 Liaison duties with other organizations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-225.md","filePath":"vacode/33.2-225.md"}'),o={name:"vacode/33.2-225.md"},r=i('<h1 id="§-33-2-225-liaison-duties-with-other-organizations" tabindex="-1">§ 33.2-225 Liaison duties with other organizations. <a class="header-anchor" href="#§-33-2-225-liaison-duties-with-other-organizations" aria-label="Permalink to &quot;§ 33.2-225 Liaison duties with other organizations.&quot;">​</a></h1><p>Tasks and responsibilities concerning transportation program or project delivery shall be carried out as follows:</p><ol><li><p>The Commissioner of Highways shall cooperate with the federal government, the American Association of State Highway and Transportation Officials, and any other organization in the numbering, signing, and marking of highways; in the taking of measures for the promotion of highway safety; in research activities; in the preparation of standard specifications; in the testing of highway materials; and otherwise with respect to transportation projects.</p></li><li><p>The Department of Transportation and the Department of Rail and Public Transportation may offer technical assistance and coordinate state resources, as available, to work with local governments, upon their request, in developing sound transportation components for their local comprehensive plans.</p></li></ol><p>2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0585" target="_blank" rel="noreferrer">585</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0646" target="_blank" rel="noreferrer">646</a>, § 33.1-190.4; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',4),n=[r];function s(l,h,c,p,d,g){return a(),t("div",null,n)}const m=e(o,[["render",s]]);export