import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 33.2-227 Defense of employees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-227.md","filePath":"vacode/33.2-227.md"}'),r={name:"vacode/33.2-227.md"},n=a('<h1 id="§-33-2-227-defense-of-employees" tabindex="-1">§ 33.2-227 Defense of employees. <a class="header-anchor" href="#§-33-2-227-defense-of-employees" aria-label="Permalink to &quot;§ 33.2-227 Defense of employees.&quot;">​</a></h1><p>If any person employed by the Commonwealth Transportation Board, the Department of Transportation, or the Department of Rail and Public Transportation is arrested or indicted or otherwise prosecuted on any charge arising out of any act committed in the discharge of his official duties, the Commissioner of Highways or the Director of the Department of Rail and Public Transportation may employ special counsel approved by the Attorney General to defend such employee. The compensation for special counsel employed, pursuant to this section, shall, subject to the approval of the Attorney General, be paid by the agency for which the employee works out of the funds appropriated for the administration of the Department of Transportation or the Department of Rail and Public Transportation.</p><p>Code 1950, § 33-11.1; 1958, c. 542; 1970, c. 322, § 33.1-11; 1974, c. 462; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0585" target="_blank" rel="noreferrer">585</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0646" target="_blank" rel="noreferrer">646</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',3),i=[n];function s(p,l,c,f,h,d){return t(),o("div",null,i)}const u=e(r,[["render",s]]);export{_ as __pageData,u as default};
