import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 33.2-235 Procurement.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-235.md","filePath":"vacode/33.2-235.md"}'),a={name:"vacode/33.2-235.md"},c=o('<h1 id="§-33-2-235-procurement" tabindex="-1">§ 33.2-235 Procurement. <a class="header-anchor" href="#§-33-2-235-procurement" aria-label="Permalink to &quot;§ 33.2-235 Procurement.&quot;">​</a></h1><p>All projects reasonably estimated to cost $300,000 or more that the Board or the Commissioner of Highways may undertake for construction shall be let in accordance with the Virginia Public Procurement Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4300/" target="_blank" rel="noreferrer">2.2-4300</a> et seq.). When such projects are reasonably estimated to cost below $300,000, the Commissioner of Highways may let them to contract, and if such projects are let to contract, they shall be let only in accordance with the Virginia Public Procurement Act.</p><p>As used in this section, &quot;project&quot; means construction and does not include routine maintenance work or the installation of traffic control devices, unless such work is to be performed under contract.</p><p>Code 1950, § 33-99; 1970, c. 322, § 33.1-185; 1973, c. 229; 1977, c. 678; 1978, c. 144; 1982, c. 647; 1992, c. 108; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0022" target="_blank" rel="noreferrer">22</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0050" target="_blank" rel="noreferrer">50</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',4),n=[c];function i(s,l,h,d,_,u){return t(),r("div",null,n)}const f=e(a,[["render",i]]);export{m as __pageData,f as default};
