import{_ as e,o as t,c as o,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 33.2-2505 Allocation of certain Authority expenses among component counties and cities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-2505.md","filePath":"vacode/33.2-2505.md"}'),a={name:"vacode/33.2-2505.md"},i=n('<h1 id="§-33-2-2505-allocation-of-certain-authority-expenses-among-component-counties-and-cities" tabindex="-1">§ 33.2-2505 Allocation of certain Authority expenses among component counties and cities. <a class="header-anchor" href="#§-33-2-2505-allocation-of-certain-authority-expenses-among-component-counties-and-cities" aria-label="Permalink to &quot;§ 33.2-2505 Allocation of certain Authority expenses among component counties and cities.&quot;">​</a></h1><p>Notwithstanding the provisions of subdivision C 1 of § <a href="/vacode/33.2-2510/">33.2-2510</a>, the administrative and operating expenses of the Authority shall be provided in an annual budget adopted by the Authority, to the extent funds for such expenses are not provided from other sources, shall be paid from the Fund. Such budget shall be limited solely to the administrative and operating expenses of the Authority and shall not include any funds for construction or acquisition of transportation facilities or for the performance of any transportation service.</p><p>2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0846" target="_blank" rel="noreferrer">846</a>, § 15.2-4835; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0749" target="_blank" rel="noreferrer">749</a>.</p>',3),r=[i];function s(c,l,d,p,h,f){return t(),o("div",null,r)}const g=e(a,[["render",s]]);export{_ as __pageData,g as default};
