import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 33.2-256 Department of Transportation established.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-256.md","filePath":"vacode/33.2-256.md"}'),o={name:"vacode/33.2-256.md"},n=r('<h1 id="§-33-2-256-department-of-transportation-established" tabindex="-1">§ 33.2-256 Department of Transportation established. <a class="header-anchor" href="#§-33-2-256-department-of-transportation-established" aria-label="Permalink to &quot;§ 33.2-256 Department of Transportation established.&quot;">​</a></h1><p>A. There is hereby created a Department of Transportation within the executive branch, which shall be under the supervision and management of the Commissioner of Highways and responsible to the Secretary of Transportation.</p><p>B. The core mission of the Department shall be as follows:</p><ol><li><p>To maintain and operate the system of state highways;</p></li><li><p>To develop, oversee, and manage highway projects included in the Six-Year Improvement Program pursuant to § <a href="http://law.lis.virginia.gov/vacode/33.2-214/" target="_blank" rel="noreferrer">33.2-214</a> based on guidance from the Commonwealth Transportation Board or funded pursuant to § <a href="http://law.lis.virginia.gov/vacode/33.2-1524/" target="_blank" rel="noreferrer">33.2-1524</a>; and</p></li><li><p>To ensure the safety of the traveling public on the system of state highways.</p></li></ol><p>Nothing in this subsection shall be construed to limit or restrict the powers otherwise granted to the Department or Commissioner.</p><p>2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0166" target="_blank" rel="noreferrer">166</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0273" target="_blank" rel="noreferrer">273</a>.</p>',6),i=[n];function s(l,h,p,c,d,f){return t(),a("div",null,i)}const g=e(o,[["render",s]]);export{_ as __pageData,g as default};
