import{_ as e,o as a,c as n,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 33.2-2605 Annual budget and allocation of expenses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-2605.md","filePath":"vacode/33.2-2605.md"}'),o={name:"vacode/33.2-2605.md"},t=i('<h1 id="§-33-2-2605-annual-budget-and-allocation-of-expenses" tabindex="-1">§ 33.2-2605 Annual budget and allocation of expenses. <a class="header-anchor" href="#§-33-2-2605-annual-budget-and-allocation-of-expenses" aria-label="Permalink to &quot;§ 33.2-2605 Annual budget and allocation of expenses.&quot;">​</a></h1><p>A. The Commission shall adopt an annual budget and develop a funding plan and shall provide for such adoption in its bylaws. The funding plan shall provide for the expenditure of funds over a four- to six-year period and shall align with the Statewide Transportation Plan established pursuant to § <a href="/vacode/33.2-353/">33.2-353</a> as much as possible. The Commission shall solicit public comment on its budget and funding plan by posting a summary of such budget and funding plan on its website and holding a public hearing. Such public hearing shall be advertised on the Commission&#39;s website and in a newspaper of general circulation in Planning District 23.</p><p>B. The administrative and operating expenses of the Commission shall be provided in an annual budget adopted by the Commission. To the extent that funds for such expenses are not provided from other sources, the expenses shall be paid from the Fund and the Regional Transit Fund on an approximately pro rata basis of the programs supported by the Fund and the Regional Transit Fund. Such budget shall be limited solely to the administrative and operating expenses of the Commission and shall not include any funds for construction or acquisition of transportation facilities or the performance of any transportation service.</p><p>C. Members may be reimbursed for all reasonable and necessary expenses provided in §§ <a href="/vacode/2.2-2813/">2.2-2813</a> and <a href="/vacode/2.2-2825/">2.2-2825</a>, if approved by the Commission. Funding for the costs of compensation and expenses of the members shall be provided by the Commission.</p><p>2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0545" target="_blank" rel="noreferrer">545</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0678" target="_blank" rel="noreferrer">678</a>; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0603" target="_blank" rel="noreferrer">603</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0608" target="_blank" rel="noreferrer">608</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1241" target="_blank" rel="noreferrer">1241</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1281" target="_blank" rel="noreferrer">1281</a>.</p>',5),r=[t];function s(l,d,p,h,c,f){return a(),n("div",null,r)}const b=e(o,[["render",s]]);export{g as __pageData,b as default};
