import{_ as o,o as a,c as s,z as e,a as t}from"./chunks/framework.9482e208.js";const k=JSON.parse('{"title":"§ 33.2-264 Livestock on right-of-way of the systems of state highways.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-264.md","filePath":"vacode/33.2-264.md"}'),r={name:"vacode/33.2-264.md"},n=e("h1",{id:"§-33-2-264-livestock-on-right-of-way-of-the-systems-of-state-highways",tabindex:"-1"},[t("§ 33.2-264 Livestock on right-of-way of the systems of state highways. "),e("a",{class:"header-anchor",href:"#§-33-2-264-livestock-on-right-of-way-of-the-systems-of-state-highways","aria-label":'Permalink to "§ 33.2-264 Livestock on right-of-way of the systems of state highways."'},"​")],-1),i=e("p",null,"No person, firm, or corporation shall pasture or graze, or cause to be pastured or grazed, or otherwise permit to be on any right-of-way of any highway in the systems of state highways, except as otherwise provided in this section, any livestock, unless such animal or animals be securely tied or held by chain or rope so as to prevent such animal from getting on the traveled portion of the highway, provided that this section shall not apply when such livestock are being driven along such highway while under the control of a responsible drover or drovers.",-1),h=e("p",null,"Nothing in this section shall prevent the owners of abutting parcels of land from grazing livestock unsecured by chain or rope on secondary roads that (i) have been taken into the system as gated roads and (ii) carry fewer than 50 vehicles per day.",-1),c=e("p",null,'On gated roads carrying 50 or more vehicles per day, the Department shall, upon the request of the local governing body and upon the recordation of a deed of gift or donation by such landowner of not less than a 40-foot right-of-way, reimburse abutting landowners a sum equal to $1 per foot of fencing that must be installed to keep cattle from entering the right-of-way from such abutting land. Where such fencing separates pasture land from a water source used by the owner of such pasture land to water his livestock, the Department shall construct or have constructed a means of access by which stock may reach the water source from the pasture land. Moneys for such fencing and construction of access to water shall be taken from highway construction funds. For purposes of this section, a "gated" road is a road on which, prior to July 1, 1986, abutting landowners have maintained a gate or cattle guard.',-1),l=e("p",null,"Any person, firm, or corporation who violates any of the provisions of this article shall be fined not less than $10 nor more than $50 for such offense.",-1),d=e("p",null,"Nothing in this section shall be construed to transfer the liability for injuries or property damage caused by such grazing livestock.",-1),f=e("p",null,[t("Code 1950, § 33-125; 1956, c. 221; 1970, c. 322, § 33.1-210; 1986, c. 367; 2014, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805",target:"_blank",rel:"noreferrer"},"805"),t(".")],-1),u=[n,i,h,c,l,d,f];function p(g,y,m,w,v,_){return a(),s("div",null,u)}const x=o(r,[["render",p]]);export{k as __pageData,x as default};
