import{_ as e,o as r,c as t,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 33.2-278 Facilities for persons desiring to fish from bridges.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-278.md","filePath":"vacode/33.2-278.md"}'),i={name:"vacode/33.2-278.md"},a=o('<h1 id="§-33-2-278-facilities-for-persons-desiring-to-fish-from-bridges" tabindex="-1">§ 33.2-278 Facilities for persons desiring to fish from bridges. <a class="header-anchor" href="#§-33-2-278-facilities-for-persons-desiring-to-fish-from-bridges" aria-label="Permalink to &quot;§ 33.2-278 Facilities for persons desiring to fish from bridges.&quot;">​</a></h1><p>The Department may, upon the request in writing of any department or agency of the Commonwealth, construct and maintain on or in connection with any bridges that now constitute a part of any system of state highways platforms, walkways, or other facilities as may be necessary or proper for the safety and convenience of persons who desire to fish therefrom. The cost shall be paid out of funds furnished by the department or agency making the request from its own funds or funds furnished to such department or agency by gift from private sources. The Department shall not be held responsible for damage caused by the construction or use of such facilities.</p><p>Code 1950, § 33-123; 1970, c. 322, § 33.1-207; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0585" target="_blank" rel="noreferrer">585</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0646" target="_blank" rel="noreferrer">646</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',3),s=[a];function n(f,c,h,d,l,p){return r(),t("div",null,s)}const _=e(i,[["render",n]]);export{m as __pageData,_ as default};
