import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 33.2-2804 Governance of Authority; composition; terms.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-2804.md","filePath":"vacode/33.2-2804.md"}'),i={name:"vacode/33.2-2804.md"},a=r('<h1 id="§-33-2-2804-governance-of-authority-composition-terms" tabindex="-1">§ 33.2-2804 Governance of Authority; composition; terms. <a class="header-anchor" href="#§-33-2-2804-governance-of-authority-composition-terms" aria-label="Permalink to &quot;§ 33.2-2804 Governance of Authority; composition; terms.&quot;">​</a></h1><p>The Authority shall be governed by a board of directors, for purposes of this chapter referred to as the &quot;Authority Board,&quot; which shall consist of the following:</p><ol><li><p>Two directors representing the County of Albemarle, each of whom shall be a member of the governing body of the county;</p></li><li><p>Two directors representing the City of Charlottesville, each of whom shall be a member of the governing body of the city;</p></li><li><p>One director representing each county that joins the Authority pursuant to § <a href="http://law.lis.virginia.gov/vacode/33.2-2803/" target="_blank" rel="noreferrer">33.2-2803</a>, each of whom shall be a member of the governing body of each respective county; and</p></li><li><p>Up to four additional directors, who shall be nonvoting, representing the interests of such agencies, institutions, and facilities described in § <a href="http://law.lis.virginia.gov/vacode/33.2-2803/" target="_blank" rel="noreferrer">33.2-2803</a> that join the Authority.</p></li></ol><p>All members of the Authority Board shall serve terms coincident with their terms of office. Vacancies shall be filled in the same manner as the original appointments.</p><p>The Authority Board shall appoint a chair and vice-chair from among its members.</p><p>2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0645" target="_blank" rel="noreferrer">645</a>, § 15.2-7027; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',6),n=[a];function s(h,l,c,p,f,d){return t(),o("div",null,n)}const _=e(i,[["render",s]]);export{g as __pageData,_ as default};
