import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 33.2-296 Revenues of the Authority.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-296.md","filePath":"vacode/33.2-296.md"}'),r={name:"vacode/33.2-296.md"},s=a('<h1 id="§-33-2-296-revenues-of-the-authority" tabindex="-1">§ 33.2-296 Revenues of the Authority. <a class="header-anchor" href="#§-33-2-296-revenues-of-the-authority" aria-label="Permalink to &quot;§ 33.2-296 Revenues of the Authority.&quot;">​</a></h1><p>All moneys received by the Authority pursuant to this article including, without limitation, moneys received from the Commonwealth Rail Fund established pursuant to § <a href="/vacode/33.2-1526.4/">33.2-1526.4</a>, whether as proceeds from the sale of bonds or as revenues, shall be deemed to be trust funds to be held and applied solely as provided in this article. The resolution authorizing the bonds of any issue or the trust indenture or agreement or resolution securing such bonds shall provide that any officer with whom, or any bank or trust company with which, such moneys shall be deposited shall act as a trustee of such moneys and shall hold and apply the same for the purposes hereof, subject to such regulations as this article and such trust indenture or agreement or resolution may provide.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1230" target="_blank" rel="noreferrer">1230</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1275" target="_blank" rel="noreferrer">1275</a>.</p>',3),n=[s];function i(h,l,u,c,d,p){return t(),o("div",null,n)}const m=e(r,[["render",i]]);export{_ as __pageData,m as default};
