import{_ as e,o as r,c as o,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 33.2-299.1 Exemption of Authority from personnel and procurement procedures.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-299.1.md","filePath":"vacode/33.2-299.1.md"}'),a={name:"vacode/33.2-299.1.md"},n=t('<h1 id="§-33-2-299-1-exemption-of-authority-from-personnel-and-procurement-procedures" tabindex="-1">§ 33.2-299.1 Exemption of Authority from personnel and procurement procedures. <a class="header-anchor" href="#§-33-2-299-1-exemption-of-authority-from-personnel-and-procurement-procedures" aria-label="Permalink to &quot;§ 33.2-299.1 Exemption of Authority from personnel and procurement procedures.&quot;">​</a></h1><p>The provisions of the Virginia Personnel Act (§ <a href="/vacode/2.2-2900/">2.2-2900</a> et seq.) and the Virginia Public Procurement Act (§ <a href="/vacode/2.2-4300/">2.2-4300</a> et seq.) shall not apply to the Authority in the exercise of any power conferred under this article. The Authority shall develop and adopt rules governing their procurement procedures. However, such rules adopted for the procurement of professional services with a cost expected to exceed $80,000 shall be consistent with the provisions of §§ <a href="/vacode/2.2-4302.2/">2.2-4302.2</a>, <a href="/vacode/2.2-4303.1/">2.2-4303.1</a> and <a href="/vacode/2.2-4303.2/">2.2-4303.2</a>. The initial rules shall be adopted by the Board no later than six months after the first meeting of the Board.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1230" target="_blank" rel="noreferrer">1230</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1275" target="_blank" rel="noreferrer">1275</a>.</p>',3),i=[n];function s(c,p,d,h,l,f){return r(),o("div",null,i)}const m=e(a,[["render",s]]);export{_ as __pageData,m as default};
