import{_ as e,o as t,c as n,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 33.2-301  Contracts for maintenance of components of Interstate System.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-301.md","filePath":"vacode/33.2-301.md"}'),a={name:"vacode/33.2-301.md"},r=o('<h1 id="§-33-2-301-contracts-for-maintenance-of-components-of-interstate-system" tabindex="-1">§ 33.2-301 Contracts for maintenance of components of Interstate System. <a class="header-anchor" href="#§-33-2-301-contracts-for-maintenance-of-components-of-interstate-system" aria-label="Permalink to &quot;§ 33.2-301  Contracts for maintenance of components of Interstate System.&quot;">​</a></h1><p>All maintenance on components of the Interstate System, excluding frontage roads, shall be carried out under contracts awarded by the Commissioner of Highways or the Board pursuant to §§ <a href="/vacode/33.2-209/">33.2-209</a>, <a href="/vacode/33.2-214/">33.2-214</a>, and <a href="/vacode/33.2-221/">33.2-221</a>, except for instances where good and sufficient reasons for not doing so have been shown in advance in writing by the Commissioner of Highways to the Board and to the Chairmen of the House Committee on Transportation, the House Committee on Appropriations, the House Committee on Finance, the Senate Committee on Transportation, and the Senate Committee on Finance and Appropriations. Nothing in this section shall be construed to prevent the Department from performing emergency work at any time on the Interstate System with its own employees or agents or to assume the maintenance responsibilities of a contractor who has been determined to be in default or as a result of a contract termination.</p><p>2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0782" target="_blank" rel="noreferrer">782</a>, § 33.1-49.1; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0729" target="_blank" rel="noreferrer">729</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0733" target="_blank" rel="noreferrer">733</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',3),i=[r];function s(c,m,f,h,l,p){return t(),n("div",null,i)}const g=e(a,[["render",s]]);export{_ as __pageData,g as default};
