import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 33.2-306 Applicability of §§ 33.2-300 through 33.2-305 to toll projects.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-306.md","filePath":"vacode/33.2-306.md"}'),r={name:"vacode/33.2-306.md"},n=a('<h1 id="§-33-2-306-applicability-of-§§-33-2-300-through-33-2-305-to-toll-projects" tabindex="-1">§ 33.2-306 Applicability of §§ 33.2-300 through 33.2-305 to toll projects. <a class="header-anchor" href="#§-33-2-306-applicability-of-§§-33-2-300-through-33-2-305-to-toll-projects" aria-label="Permalink to &quot;§ 33.2-306 Applicability of §§ 33.2-300 through 33.2-305 to toll projects.&quot;">​</a></h1><p>The provisions of §§ <a href="http://law.lis.virginia.gov/vacode/33.2-300/" target="_blank" rel="noreferrer">33.2-300</a> through <a href="http://law.lis.virginia.gov/vacode/33.2-305/" target="_blank" rel="noreferrer">33.2-305</a> shall not become effective with respect to those segments of the Interstate System constructed and financed as toll projects until the revenue bonds and the interest thereon issued on account of such toll projects have been paid or a sufficient amount for the payment of all such bonds and the interest to maturity thereon has been set aside in trust for the benefit of the respective bondholders. When the bonds and interest thereon, outstanding on account of such projects, have been paid or a sufficient amount for the payment of such bonds and the interest thereon to the maturity thereof has been so set aside in trust, and when the Board has by formal action, recorded in its minutes, determined the existence of such fact, then the provisions of §§ <a href="http://law.lis.virginia.gov/vacode/33.2-300/" target="_blank" rel="noreferrer">33.2-300</a> through <a href="http://law.lis.virginia.gov/vacode/33.2-308/" target="_blank" rel="noreferrer">33.2-308</a> shall fully apply to such projects.</p><p>Code 1950, § 33-36.8; 1958, c. 589; 1970, c. 322, § 33.1-54; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>.</p>',3),i=[n];function s(h,l,c,p,d,f){return t(),o("div",null,i)}const g=e(r,[["render",s]]);export{_ as __pageData,g as default};
