import{_ as e,o as t,c as o,O as s}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 33.2-3100.1 Metro Reform Commission established; membership; duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-3100.1.md","filePath":"vacode/33.2-3100.1.md"}'),r={name:"vacode/33.2-3100.1.md"},a=s('<h1 id="§-33-2-3100-1-metro-reform-commission-established-membership-duties" tabindex="-1">§ 33.2-3100.1 Metro Reform Commission established; membership; duties. <a class="header-anchor" href="#§-33-2-3100-1-metro-reform-commission-established-membership-duties" aria-label="Permalink to &quot;§ 33.2-3100.1 Metro Reform Commission established; membership; duties.&quot;">​</a></h1><p>A. As used in this chapter, unless the context requires a different meaning:</p><p>&quot;Commission&quot; means the Metro Reform Commission.</p><p>&quot;WMATA&quot; means the Washington Metropolitan Area Transit Authority.</p><p>B. There is hereby created the Metro Reform Commission. The Commission shall consist of four members appointed as follows: two members appointed by the Speaker of the House of Delegates and two members appointed by the Senate Committee on Rules. Members of the Commission may or may not be members of the General Assembly. Members shall be citizens of the Commonwealth, but shall not be required to reside in the area served by WMATA. Members shall serve without compensation, but shall be entitled to be reimbursed for all reasonable and necessary expenses incurred in the performance of their duties pursuant to §§ <a href="/vacode/2.2-2813/">2.2-2813</a> and <a href="/vacode/2.2-2825/">2.2-2825</a>.</p><p>C. The Commission shall advise and make recommendations to the Signatories of the Washington Metropolitan Area Transit Authority Compact of 1966 on reforms to the National Capital Area Interest Arbitration Standards Act.</p><p>2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0854" target="_blank" rel="noreferrer">854</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0856" target="_blank" rel="noreferrer">856</a>.</p>',7),i=[a];function n(m,h,l,d,p,c){return t(),o("div",null,i)}const u=e(r,[["render",n]]);export{b as __pageData,u as default};
