import{_ as a,o as n,c as s,z as e,a as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 33.2-327 Design standards for secondary state highway system components.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-327.md","filePath":"vacode/33.2-327.md"}'),o={name:"vacode/33.2-327.md"},i=e("h1",{id:"§-33-2-327-design-standards-for-secondary-state-highway-system-components",tabindex:"-1"},[t("§ 33.2-327 Design standards for secondary state highway system components. "),e("a",{class:"header-anchor",href:"#§-33-2-327-design-standards-for-secondary-state-highway-system-components","aria-label":'Permalink to "§ 33.2-327 Design standards for secondary state highway system components."'},"​")],-1),r=e("p",null,"For urban and urban development areas in localities using the urban county executive form of government, the Department shall work in conjunction with the locality and the Department of Rail and Public Transportation to review new design standards for secondary state highway system components that the locality proposes. Such standards shall (i) be based on the American Association of State Highway and Transportation Officials (AASHTO) A Policy on Geometric Design of Highways and Streets and other publications applicable to urban areas; (ii) set forth a design methodology that should be used in the affected urban and urban development areas; (iii) allow for the efficient movement of transit and other vehicles through these areas; (iv) accommodate safe pedestrian and bicyclist movement; (v) accommodate high density urban development; (vi) encourage user-friendly access to transit; (vii) include stormwater management guidelines, consistent with state and local laws and regulations; and (viii) respect the character of urban areas. These design standards and methodologies are intended to facilitate approval of roadway and transportation system improvement plans in urban areas that comply with the standards. These design standards shall not contradict or be in conflict with the principles outlined in the applicable Board regulations concerning terms and conditions under which subdivision streets may be accepted into the secondary state highway system.",-1),d=e("p",null,"Standards developed by parties as required by this section shall be submitted to the Department for final review and approval at least three months prior to the locality's anticipated implementation date.",-1),c=e("p",null,[t("2010, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0498",target:"_blank",rel:"noreferrer"},"498"),t(", § 33.1-69.001; 2014, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805",target:"_blank",rel:"noreferrer"},"805"),t(".")],-1),l=[i,r,d,c];function h(p,m,f,g,u,y){return n(),s("div",null,l)}const _=a(o,[["render",h]]);export{v as __pageData,_ as default};
