import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 33.2-330 Relocation or removal of utility facilities within secondary state highway system construction projects.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-330.md","filePath":"vacode/33.2-330.md"}'),a={name:"vacode/33.2-330.md"},r=i('<h1 id="§-33-2-330-relocation-or-removal-of-utility-facilities-within-secondary-state-highway-system-construction-projects" tabindex="-1">§ 33.2-330 Relocation or removal of utility facilities within secondary state highway system construction projects. <a class="header-anchor" href="#§-33-2-330-relocation-or-removal-of-utility-facilities-within-secondary-state-highway-system-construction-projects" aria-label="Permalink to &quot;§ 33.2-330 Relocation or removal of utility facilities within secondary state highway system construction projects.&quot;">​</a></h1><p>A. As used in this section:</p><p>&quot;Cost of highway construction&quot; includes the cost of relocating or removing utility facilities in connection with any project on the secondary state highway system.</p><p>&quot;Cost of relocation or removal&quot; includes the entire amount paid by such utility properly attributable to such relocation or removal after deducting therefrom any increase in the value of the new facility and any salvage value derived from the old facility.</p><p>&quot;Facility of a utility&quot; includes tracks, pipes, mains, conduits, cables, wires, towers, and other structures, equipment, and appliances.</p><p>&quot;Utility&quot; includes utilities owned by a county, city, town, or public authority, and nonprofit, consumer-owned company located in a county having a population of at least 30,000 but no more than 34,000 that (i) is exempt from income taxation under § 501(c)(3) of the Internal Revenue Code, (ii) is organized to provide suitable drinking water, (iii) has no assistance from investors, (iv) does not pay dividends, and (v) does not sell stock to the general public.</p><p>B. Whenever it is necessary that the facility of a utility in, on, under, over, or along an existing highway that is to be included within any construction project on the secondary state highway system should be relocated or removed, the owner or operator of such facility shall relocate or remove the same in accordance with the order of the Board. The cost of such relocation or removal, including the cost of installing such facility in a new location, and the cost of any lands, or any rights or interest in lands, and any other rights, required to accomplish such relocation or removal shall be ascertained and paid by the Board as a part of the cost of such project.</p><p>1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0680" target="_blank" rel="noreferrer">680</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0688" target="_blank" rel="noreferrer">688</a>, § 33.1-69.2; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0942" target="_blank" rel="noreferrer">942</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0805" target="_blank" rel="noreferrer">805</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0168" target="_blank" rel="noreferrer">168</a>.</p>',8),n=[r];function s(c,l,h,d,u,p){return t(),o("div",null,n)}const g=e(a,[["render",s]]);export{y as __pageData,g as default};
