import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 33.2-3308 Other duties and responsibilities of Authority.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/33.2-3308.md","filePath":"vacode/33.2-3308.md"}'),r={name:"vacode/33.2-3308.md"},a=o('<h1 id="§-33-2-3308-other-duties-and-responsibilities-of-authority" tabindex="-1">§ 33.2-3308 Other duties and responsibilities of Authority. <a class="header-anchor" href="#§-33-2-3308-other-duties-and-responsibilities-of-authority" aria-label="Permalink to &quot;§ 33.2-3308 Other duties and responsibilities of Authority.&quot;">​</a></h1><p>In addition to other powers herein granted, the Authority shall have the following duties and responsibilities:</p><ol><li><p>General oversight of Williamsburg area programs involving mass transit or congestion mitigation;</p></li><li><p>Long-range transit planning in the Williamsburg area, both financially constrained and financially unconstrained;</p></li><li><p>Recommending to state, regional, and federal agencies regional transit priorities, including public-private transit projects and funding allocations;</p></li><li><p>Allocating to priority regional transit projects any funds made available to the Authority and, at the discretion of the Authority, directly overseeing such projects;</p></li><li><p>Recommending to the Commonwealth Transportation Board priority regional transit projects for receipt of federal and state funds;</p></li><li><p>Serving as an advocate for the transit needs of the Williamsburg area before the state and federal governments;</p></li><li><p>Applying to and negotiating with the government of the United States, the Commonwealth, or any agency or instrumentality thereof, for grants and any other funds available to carry out the purposes of this chapter and receiving, holding, accepting, and administering from any source gifts, bequests, grants, aid, or contributions of money, property, labor, or other things of value to be held, used, and applied to carry out the purposes of this chapter subject, however, to any conditions upon which gifts, bequests, grants, aid, or contributions are made. Unless otherwise restricted by the terms of the gift, bequest, or grant, the Authority may sell, exchange, or otherwise dispose of such money, securities, or other property given or bequeathed to it in furtherance of its purposes.</p></li></ol><p>2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0179" target="_blank" rel="noreferrer">179</a>, § 15.2-6809; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>.</p>',4),n=[a];function s(l,h,d,p,c,g){return t(),i("div",null,n)}const m=e(r,[["render",s]]);export{f as __pageData,m as default};
